import React, { useState } from "react";
import HexButton from "../components/HexButton";
import TicTacToe from "../games/TicTacToe/TicTacToe";
// import Minesweeper from "./Minesweeper";

const GamePage = () => {
  const [selectedGame, setSelectedGame] = useState(null);

  const games = [
    { name: "Tic-Tac-Toe", component: <TicTacToe /> },
    { name: "Coming Soon...", component: null },
    { name: "Coming Soon...", component: null },
    { name: "Coming Soon...", component: null },
    { name: "Coming Soon...", component: null },
    { name: "Coming Soon...", component: null },
  ];

  const totalGames = games.length;

  return (
    <div className="flex mx-[20vh] my-[5vh] h-[80vh] ">
      <div className="flex flex-wrap w-1/5 gap-[2vh] justify-center items-center">
        {[...games].map((game, index) => (
          <div key={index}>
            <HexButton
              text={game.name}
              onClickAction={() => setSelectedGame(game.name)}
            />
          </div>
        ))}
      </div>

      {/* Main Content */}
      <div className="hex-main-screen flex w-3/5 justify-center items-center border-8 border-double border-primary">
        <h2 className="text-2xl font-bold">
          {selectedGame &&
          games.find((game) => game.name === selectedGame)?.component
            ? games.find((game) => game.name === selectedGame)?.component
            : "Select A Game"}
        </h2>
      </div>
    </div>
  );
};

export default GamePage;
