import React, { useState } from "react";
import "./TicTacToe.css"; // Import styles

const TicTacToe = () => {
  const SYMBOL_X = "X";
  const SYMBOL_O = "O";
  const [gridSize, setGridSize] = useState(3);
  const [winCondition, setWinCondition] = useState(3);
  const [gameStarted, setGameStarted] = useState(false);
  const [squares, setSquares] = useState(Array(gridSize * gridSize).fill(null));
  const [winner, setWinner] = useState(null);
  const [currentTurn, setCurrentTurn] = useState(SYMBOL_X);

  const handleClick = (index) => {
    if (squares[index] || winner) return;
    const newSquares = squares.slice();
    newSquares[index] = currentTurn;
    setSquares(newSquares);
    setCurrentTurn(currentTurn === SYMBOL_X ? SYMBOL_O : SYMBOL_X);
    setWinner(checkWinner(newSquares));
  };

  const checkWinner = (board) => {
    const winningLines = generateWinningLines(gridSize, winCondition);
    for (let line of winningLines) {
      const values = line.map((i) => board[i]);
      if (values.every((v) => v && v === values[0])) {
        return values[0];
      }
    }
    return null;
  };

  const generateWinningLines = (size, winLength) => {
    let lines = [];
    for (let row = 0; row < size; row++) {
      for (let col = 0; col <= size - winLength; col++) {
        lines.push([...Array(winLength)].map((_, i) => row * size + col + i));
      }
    }
    for (let col = 0; col < size; col++) {
      for (let row = 0; row <= size - winLength; row++) {
        lines.push([...Array(winLength)].map((_, i) => (row + i) * size + col));
      }
    }
    for (let row = 0; row <= size - winLength; row++) {
      for (let col = 0; col <= size - winLength; col++) {
        lines.push([...Array(winLength)].map((_, i) => (row + i) * size + col + i));
        lines.push([...Array(winLength)].map((_, i) => (row + i) * size + col + winLength - 1 - i));
      }
    }
    return lines;
  };

  const resetGame = () => {
    setSquares(Array(gridSize * gridSize).fill(null));
    setCurrentTurn(SYMBOL_X);
    setWinner(null);
  };

  const restartGame = () => {
    setGridSize(3);
    setWinCondition(3);
    setGameStarted(false);
    resetGame();
  };

  return (
    <div className="flex flex-col items-center space-y-4 p-4">
      {!gameStarted ? (
        <div className="flex flex-col space-y-4">
          <label className="text-lg font-bold">Grid Size:</label>
          <input
            type="number"
            value={gridSize}
            onChange={(e) => setGridSize(Number(e.target.value))}
            className="p-2 border rounded"
            min="3"
          />
          <label className="text-lg font-bold">Win Condition:</label>
          <input
            type="number"
            value={winCondition}
            onChange={(e) => setWinCondition(Number(e.target.value))}
            className="p-2 border rounded"
            min="3"
            max={gridSize}
          />
          <button
            className="bg-blue-500 text-white px-4 py-2 rounded hover:bg-blue-700"
            onClick={() => setGameStarted(true)}
          >
            Start Game
          </button>
        </div>
      ) : (
        <>
          <div className="grid-container" style={{ gridTemplateColumns: `repeat(${gridSize}, 80px)` }}>
            {squares.map((square, i) => (
              <button
                key={i}
                className={`square ${winner && winner.includes(i) ? "winning-square" : ""}`}
                onClick={() => handleClick(i)}
              >
                {square}
              </button>
            ))}
            {/* Hash Effect */}
            <div className="hash-line horizontal-1"></div>
            <div className="hash-line horizontal-2"></div>
            <div className="hash-line vertical-1"></div>
            <div className="hash-line vertical-2"></div>
          </div>
          {winner && <p className="text-xl font-bold text-red-500">Winner: {winner}</p>}
          <div className="flex space-x-4">
            <button className="bg-yellow-500 text-white px-4 py-2 rounded hover:bg-yellow-700" onClick={resetGame}>
              Restart
            </button>
            <button className="bg-red-500 text-white px-4 py-2 rounded hover:bg-red-700" onClick={restartGame}>
              Reset
            </button>
          </div>
        </>
      )}
    </div>
  );
};

export default TicTacToe;
