import React, { useState } from "react";
import "./TicTacToe.css"; // Import styles

const TicTacToe = () => {
  const SYMBOL_X = "X";
  const SYMBOL_O = "O";
  const gridSize = 3;
  const winLength = 3;
  const [squares, setSquares] = useState(Array(3 * 3).fill(null));
  const [winner, setWinner] = useState(null);
  const [currentTurn, setCurrentTurn] = useState(SYMBOL_X);
  const [winnerLine, setWinnerLine] = useState([]);

  const resetGame = () => {
    setSquares(Array(gridSize * gridSize).fill(null));
    setCurrentTurn(SYMBOL_X);
    setWinner(null);
  };

  const drawSymbol = (index) => {
    if (squares[index] || winner) return;
    const newSquares = squares.slice();
    newSquares[index] = currentTurn;
    setSquares(newSquares);
    setCurrentTurn(currentTurn === SYMBOL_X ? SYMBOL_O : SYMBOL_X);
    setWinner(checkWinner(newSquares));
  };

  const checkWinner = (board) => {
    const winningLines = generateWinPatterns(gridSize, winLength);
    for (let line of winningLines) {
      const values = line.map((i) => board[i]);
      if (values.every((v) => v && v === values[0])) {
        setWinnerLine(line);
        return values[0];
      }
    }
    return null;
  };

  // Generate all possible winning patterns
  const generateWinPatterns = (size, winLength) => {
    let lines = [];
    // for 
    for (let row = 0; row < size; row++) {
      for (let col = 0; col <= size - winLength; col++) {
        lines.push([...Array(winLength)].map((_, i) => row * size + col + i));
      }
    }
    for (let col = 0; col < size; col++) {
      for (let row = 0; row <= size - winLength; row++) {
        lines.push([...Array(winLength)].map((_, i) => (row + i) * size + col));
      }
    }
    for (let row = 0; row <= size - winLength; row++) {
      for (let col = 0; col <= size - winLength; col++) {
        lines.push([...Array(winLength)].map((_, i) => (row + i) * size + col + i));
        lines.push([...Array(winLength)].map((_, i) => (row + i) * size + col + winLength - 1 - i));
      }
    }
    return lines;
  };

  const renderOningGameScreen = () => {
    return (
      <div>
        {
          <>
          {winner ? <p className="text-xl font-bold text-purple-900">Winner: {winner}</p>
          :
          <p className="text-xl font-bold text-purple-900">Turn: {currentTurn}</p>}
          <div className="grid-container" style={{ gridTemplateColumns: `repeat(${gridSize}, 80px)` }}>
          { squares.map((square, i) => (
            <button
              key={i}
              className={`square ${winner && winnerLine.includes(i) ? "winning-square" : ""}`}
              onClick={() => drawSymbol(i)}
            >
              {square}
            </button>
          ))}
          {/* Draw a Hash */}
          <div className="hash-line horizontal-1"></div>
          <div className="hash-line horizontal-2"></div>
          <div className="hash-line vertical-1"></div>
          <div className="hash-line vertical-2"></div>
        </div>

          <div className="flex space-x-4">
            <button className="bg-yellow-500 text-white px-4 py-2 rounded hover:bg-yellow-700" onClick={resetGame}>
              Restart
            </button>
          </div>
        </>
        }
      </div>
    );
  };


  return (
    <div className="flex flex-col items-center space-y-4 p-4">
      {renderOningGameScreen()}
    </div>
  );
};

export default TicTacToe;
