import React, { useState } from "react";
import TicTacToe from "../games/TicTacToe/TicTacToe";
// import Minesweeper from "./Minesweeper";

const GamePage = () => {
  const [selectedGame, setSelectedGame] = useState(null);

  const games = [
    { name: "Tic-Tac-Toe", component: <TicTacToe /> },
    // { name: "Minesweeper", component: <Minesweeper /> },
  ];

  return (
    <div>
      <div className="bg-white text-white p-4 relative">
        <div className="flex w-max">
          {[...games, ...games].map((game, index) => (
            <div
              key={index}
              className="flex items-center justify-center bg-yellow-500 mx-2 shrink-0 rounded-lg hover:bg-blue-500"
            >
              <button
                className=" hex-button w-80 h-32 text-center font-bold"
                onClick={() => setSelectedGame(game.name)}
              >
                {game.name}
              </button>
            </div>
          ))}
        </div>
      </div>

      {/* Main Content */}
      <div className="flex-4 p-4 flex justify-center items-center bg-white-100">
        {selectedGame ? (
          games.find((game) => game.name === selectedGame)?.component
        ) : (
          <h2 className="text-2xl font-bold">Welcome to Christy's game platform</h2>
        )}
      </div>
    </div>
  );
};

export default GamePage;
