import React, { useState } from "react";
import TicTacToe from "../games/TicTacToe/TicTacToe";
// import Minesweeper from "./Minesweeper";

const GamePage = () => {
  const [selectedGame, setSelectedGame] = useState(null);

  const games = [
    { name: "Tic-Tac-Toe", component: <TicTacToe /> },
    // { name: "Minesweeper", component: <Minesweeper /> },
  ];

  return (
    <div>
      <div className="bg-gray-800 text-white p-4 overflow-hidden relative">
        <div className="flex w-max animate-marquee hover:animate-paused">
          {[...games, ...games].map((game, index) => (
            <div
              key={index}
              className="w-40 h-32 flex items-center justify-center bg-gray-700 mx-2 rounded-lg shrink-0"
            >
              <button className="text-center">{game.name}</button>
            </div>
          ))}
        </div>
      </div>

      {/* Main Content */}
      <div className="flex-4 p-4 flex justify-center items-center bg-white-100">
        {selectedGame ? (
          games.find((game) => game.name === selectedGame)?.component
        ) : (
          <h2 className="text-2xl font-bold">Select a Game</h2>
        )}
      </div>
    </div>
  );
};

export default GamePage;
