import React, { useState } from "react";
import HexButton from "../components/HexButton";
import TicTacToe from "../games/TicTacToe/TicTacToe";

const GamePage = () => {
  const [selectedGame, setSelectedGame] = useState(null);

  const games = [
    { name: "Tic-Tac-Toe", component: <TicTacToe /> },
    { name: "Coming Soon 1", component: null },
  ];

  return (
    <div className="flex mx-[5vh] my-[5vh] h-[80vh] ">
      {/* <div className="flex w-2/5 grid grid-cols-3 place-content-start gap-40 justify-center items-center">
        {[...games].map((game, index) => {
          let className = "";
          if (index == 3) {
            className = "ml-20 -mt-20";
          }

          return (
            <div className={className} key={index}>
              <HexButton
                text={game.name}
                onClickAction={() => setSelectedGame(game.name)}
              />
            </div>
          );
        })}
      </div> */}

      <div className="flex flex-col items-center gap-4 pr-20">
        {[...Array(Math.ceil(games.length / 1))].map((_, rowIndex) => {
          const rowGames = games.slice(rowIndex * 3, rowIndex * 3 + 3);
          const isStaggered = rowIndex % 2 !== 0;

          return (
            <div
              key={rowIndex}
              className={`flex gap-4 ${isStaggered ? "ml-10" : ""}`}
            >
              {rowGames.map((game, index) => (
                <div key={index}>
                  <HexButton
                    text={game.name}
                    onClickAction={() => setSelectedGame(game.name)}
                  />
                </div>
              ))}
            </div>
          );
        })}
      </div>

      {/* Main Content */}
      <div className="flex w-3/5 justify-center items-center border-8 border-double border-primary absolute right-0">
        <h2 className="text-2xl font-bold min-h-50">
          {selectedGame &&
          games.find((game) => game.name === selectedGame)?.component
            ? games.find((game) => game.name === selectedGame)?.component
            : "Select a Game"}
        </h2>
      </div>
    </div>
  );
};

export default GamePage;
