import React, { useState } from "react";
import TicTacToe from "../games/TicTacToe/TicTacToe";
// import Minesweeper from "./Minesweeper";


const WelcomePage = () => {
  const [selectedGame, setSelectedGame] = useState(null);

  const games = [
    { name: "Tic-Tac-Toe", component: <TicTacToe /> },
    // { name: "Minesweeper", component: <Minesweeper /> },
  ];

  return (
    <>
      <div
        id="Welcome Message"
        className="flex justify-center items-center h-screen"
      >
        <h2 className="text-3xl font-bold">Welcome to the Game Platform</h2>
        <button className="px-6 py-3 bg-yellow-500 text-white text-lg font-bold rounded hover:bg-blue-700">
          Let's Start!
        </button>

        <ul>
          {games.map((game, index) => (
            <li
              key={index}
              className={`p-2 cursor-pointer bg-yellow-500 hover:bg-blue-600 text-white text-lg rounded ${
                selectedGame === game.name ? "bg-yellow-600" : ""
              }`}
              onClick={() => setSelectedGame(game.name)}
            >
              {game.name}
            </li>
          ))}
        </ul>


      </div>
    </>
  );
};

export default WelcomePage;
