import buzzThumbnail from "../media/buzz.png";
import TicTacToe from "../games/TicTacToe/TicTacToe";

const WelcomePage = () => {
  const skillWidth = 111;
  const margin = 80;
  const windowWidth = 900;

  // Calculate how many games can fit in one line
  const maxGamesInOneList = Math.floor((windowWidth - margin * 2) / skillWidth);
  const games = [
    { name: "Tic-Tac-Toe", component: <TicTacToe /> },
    { name: "Coming Soon...", component: null },
    { name: "Coming Soon...", component: null },
    { name: "Coming Soon...", component: null },
    { name: "Coming Soon...", component: null },
    { name: "Coming Soon...", component: null },
  ];

  // create JSX elements
  let gameItems = games.map((game, i) => {
    return (
      <li key={i} className="game" data-name={game.name}>
        <img src={game.img} alt={game.name} />
      </li>
    );
  });

  let gameLists = [];

  // Figure out the number of lists needed
  let numberOfLists = Math.round(gameItems.length / (maxGamesInOneList - 0.5));

  // Add the correct number of lists to game Lists
  while (gameLists.length < numberOfLists) {
    gameLists.push("list");
  }

  // Put all the skills that can fit in the current list “i” into the list
  let currentGame = 0;
  let numberOfGamesInLastList = 0;
  let numberOfGamesInThisList = 0;
  gameLists = gameLists.map((gameList, i) => {
    numberOfGamesInThisList = 0;
    let gamesInList = [];

    let maxNumberOfGamesInThisList = maxGamesInOneList;
    if (i % 2 !== 0) {
      maxNumberOfGamesInThisList--;
    }

    return (
      <ul className="skill-list" key={i}>
        {gamesInList}
      </ul>
    );
  });

  return (
    <>
      {/* <div id="Welcome Message" className="flex justify-center items-center">
        <img src={buzzThumbnail} alt="buzz..." />
      </div> */}

      <div class="lists-container">
        <ul>
          <li></li>
          <li></li>
          <li></li>
        </ul>
        <ul>
          <li></li>
          <li></li>
        </ul>
        <ul>
          <li></li>
          <li></li>
          <li></li>
        </ul>
      </div>
    </>
  );
};

export default WelcomePage;
