import { useState } from 'react';

const TicTacToe = () => {
  const gridSize = 3; // 3x3 grid
  const [board, setBoard] = useState(Array(gridSize * gridSize).fill(null));
  const [isXNext, setIsXNext] = useState(true);
  const [winner, setWinner] = useState(null);
  const [winningSquares, setWinningSquares] = useState([]);

  // Dynamically generate the winning lines for a grid of size 'gridSize'
  const getWinningLines = (size) => {
    const lines = [];

    // Rows
    for (let row = 0; row < size; row++) {
      const rowLine = [];
      for (let col = 0; col < size; col++) {
        rowLine.push(row * size + col);
      }
      lines.push(rowLine);
    }

    // Columns
    for (let col = 0; col < size; col++) {
      const colLine = [];
      for (let row = 0; row < size; row++) {
        colLine.push(row * size + col);
      }
      lines.push(colLine);
    }

    // Diagonals
    const diagonal1 = [];
    const diagonal2 = [];
    for (let i = 0; i < size; i++) {
      diagonal1.push(i * size + i);
      diagonal2.push((i + 1) * size - (i + 1));
    }
    lines.push(diagonal1);
    lines.push(diagonal2);

    return lines;
  };

  // Check for winner
  const checkWinner = (board) => {
    const lines = getWinningLines(gridSize);

    for (let i = 0; i < lines.length; i++) {
      const [a, b, c] = lines[i];
      if (board[a] && board[a] === board[b] && board[a] === board[c]) {
        setWinningSquares([a, b, c]);
        return board[a];
      }
    }
    return null;
  };

  const handleClick = (index) => {
    if (board[index] || winner) return;

    const newBoard = board.slice();
    newBoard[index] = isXNext ? 'X' : 'O';
    setBoard(newBoard);
    setIsXNext(!isXNext);

    const gameWinner = checkWinner(newBoard);
    if (gameWinner) {
      setWinner(gameWinner);
    }
  };

  const renderSquare = (index) => {
    const isWinningSquare = winningSquares.includes(index);
    return (
      <button
        className={`w-20 h-20 text-4xl flex items-center justify-center border-2 border-black cursor-pointer
          ${isWinningSquare ? 'text-red-600 font-bold animate-pulse' : ''}`}
        onClick={() => handleClick(index)}
      >
        {board[index]}
      </button>
    );
  };

  const resetGame = () => {
    setBoard(Array(gridSize * gridSize).fill(null));
    setIsXNext(true);
    setWinner(null);
    setWinningSquares([]);
  };

  return (
    <div className="flex flex-col items-center p-6">
      <div className="grid grid-cols-3 gap-2">
        {board.map((_, index) => renderSquare(index))}
      </div>
      <div className="mt-4 text-2xl">
        {winner ? `${winner} wins!` : `Next player: ${isXNext ? 'X' : 'O'}`}
      </div>
      <button
        className="mt-4 px-6 py-2 bg-green-500 text-white font-semibold rounded hover:bg-green-400"
        onClick={resetGame}
      >
        Reset Game
      </button>
    </div>
  );
};

export default TicTacToe;
